// ============================================================================
// SEED FUNCTION
// ============================================================================

export async function seedCountryConfigs(db: FirebaseFirestore.Firestore): Promise<{ success: number; failed: number; total: number }> {
  const batch = db.batch();
  let success = 0;
  let failed = 0;

  console.log(`Seeding ${ALL_COUNTRIES.length} country configurations...`);

  for (const country of ALL_COUNTRIES) {
    try {
      const docRef = db.collection('country_fiscal_configs').doc(country.countryCode);
      batch.set(docRef, {
        ...country,
        createdAt: FieldValue.serverTimestamp(),
        updatedAt: FieldValue.serverTimestamp()
      }, { merge: true });
      success++;
    } catch (error) {
      console.error(`Failed to add ${country.countryCode}:`, error);
      failed++;
    }
  }

  try {
    await batch.commit();
    console.log(`Successfully seeded ${success} countries. Failed: ${failed}`);
  } catch (error) {
    console.error('Batch commit failed:', error);
    throw error;
  }

  return { success, failed, total: ALL_COUNTRIES.length };
}

// ============================================================================
// CALLABLE FUNCTION TO TRIGGER SEED
// ============================================================================

// Lazy initialization pattern to avoid initialization during deployment analysis
const IS_DEPLOYMENT_ANALYSIS =
  !process.env.K_REVISION &&
  !process.env.K_SERVICE &&
  !process.env.FUNCTION_TARGET &&
  !process.env.FUNCTIONS_EMULATOR;

let _initialized = false;
function ensureInitialized() {
  if (!_initialized && !IS_DEPLOYMENT_ANALYSIS) {
    if (!admin.apps.length) {
      admin.initializeApp();
    }
    _initialized = true;
  }
}

export async function runSeed(): Promise<void> {
  ensureInitialized();
  const db = admin.firestore();
  await seedCountryConfigs(db);
}

// Export for use
export { ALL_COUNTRIES };
