// Process each language file
for (const lang of langs) {
  const filePath = path.join(HELPER_DIR, lang + '.json');
  let data = JSON.parse(fs.readFileSync(filePath, 'utf-8'));
  const beforeCount = Object.keys(data).length;

  // 1. Remove admin.* keys (admin = fr+en only)
  let removedAdmin = 0;
  for (const key of Object.keys(data)) {
    if (key.startsWith('admin.')) {
      delete data[key];
      removedAdmin++;
    }
  }

  // 2. Add missing non-admin keys from translation map
  let addedCount = 0;
  for (const [key, vals] of Object.entries(translations)) {
    if (vals[lang] && !(key in data)) {
      data[key] = vals[lang];
      addedCount++;
    }
  }

  // 3. Sort and write
  const sorted = {};
  for (const k of Object.keys(data).sort()) sorted[k] = data[k];
  fs.writeFileSync(filePath, JSON.stringify(sorted, null, 2) + '\n');

  const afterCount = Object.keys(sorted).length;
  console.log(`${lang}.json: ${beforeCount} → ${afterCount} (removed ${removedAdmin} admin, added ${addedCount} translations)`);
}

// Verify remaining gaps
console.log('\n=== Remaining gaps ===');
for (const lang of langs) {
  const data = JSON.parse(fs.readFileSync(path.join(HELPER_DIR, lang + '.json'), 'utf-8'));
  const missing = nonAdminKeys.filter(k => !(k in data));
  console.log(`${lang}: ${missing.length} still missing`);
}
